async function displayInfo(){const e=document.querySelectorAll(".elem");for(let t=0;t<e.length;t++)await showAfter(1e3).then(()=>{e[t].classList.add("fadeIn"),e[t].style.visibility="visible"})}function showAfter(e){return new Promise(t=>{setTimeout(()=>{t()},e)})}displayInfo(),document.getElementById("photo").addEventListener("contextmenu",e=>e.preventDefault());